import { NextResponse } from 'next/server';
import { cookies } from 'next/headers';

export async function POST() {
    const cookieStore = await cookies();
    const baseUrl = process.env.NEXT_PUBLIC_APP_URL || 'http://localhost:3000';

    // Clear all auth cookies
    cookieStore.delete('customer_access_token');
    cookieStore.delete('customer_refresh_token');

    return NextResponse.json({ success: true });
}

export async function GET() {
    const cookieStore = await cookies();
    const baseUrl = process.env.NEXT_PUBLIC_APP_URL || 'http://localhost:3000';

    // Clear all auth cookies
    const response = NextResponse.redirect(new URL('/', baseUrl));
    response.cookies.delete('customer_access_token');
    response.cookies.delete('customer_refresh_token');

    return response;
}

import { NextRequest, NextResponse } from 'next/server';
import { cookies } from 'next/headers';
import { getCustomer } from '@/lib/customerAuth.server';

export async function GET(request: NextRequest) {
    try {
        const cookieStore = await cookies();
        const accessToken = cookieStore.get('customer_access_token')?.value;

        if (!accessToken) {
            return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
        }

        const customer = await getCustomer(accessToken);

        if (!customer) {
            return NextResponse.json({ error: 'Customer not found' }, { status: 404 });
        }

        return NextResponse.json({ customer });
    } catch (error) {
        console.error('Error fetching customer profile:', error);
        return NextResponse.json({ error: 'Internal Server Error' }, { status: 500 });
    }
}

import { Metadata } from 'next';
import { notFound } from 'next/navigation';
import { ProductGrid, ProductGridSkeleton } from '@/components/ProductGrid';
import {
    Select,
    SelectContent,
    SelectItem,
    SelectTrigger,
    SelectValue,
} from '@/components/ui/select';
import { shopifyFetch, extractNodes, ShopifyProduct, ShopifyCollection } from '@/lib/shopify';
import { GET_COLLECTION_PRODUCTS, GET_PRODUCTS } from '@/lib/queries';
import { generateCollectionMetadata } from '@/lib/seo';
import { Suspense } from 'react';
import { VideoSection } from '@/components/VideoSection';

interface CollectionPageProps {
    params: Promise<{ handle: string }>;
    searchParams: Promise<{ sort?: string }>;
}

const KNOWN_COLLECTIONS: Record<string, string> = {
    'home-kitchen': 'Home & Kitchen',
    'electronics': 'Electronics',
    'packages': 'Packages',
    'services': 'Services',
    'cookware': 'Cookware',
    'appliances': 'Appliances',
    'decor': 'Home Decor',
    'sale': 'Sale',
    'lawn-care': 'Lawn Care',
    'pests': 'Pest Control',
    'seasonal': 'Seasonal Advice',
    'all': 'All Products'
};

const VIDEO_MAP: Record<string, string> = {
    'home-kitchen': '/videos/home & kitchen.mp4',
    'electronics': '/videos/electronics.mp4',
    'cookware': '/videos/cookware.mp4',
    'appliances': '/videos/appliances.mp4',
    'decor': '/videos/home decor.mp4',
};

async function getCollectionProducts(
    handle: string,
    sortKey: string = 'COLLECTION_DEFAULT',
    reverse: boolean = false
) {
    try {
        const data = await shopifyFetch<{
            collection: ShopifyCollection & {
                products: { edges: { node: ShopifyProduct }[] };
            };
        }>({
            query: GET_COLLECTION_PRODUCTS,
            variables: { handle, first: 50, sortKey, reverse },
            tags: ['collection', handle],
        });
        return data.collection;
    } catch (error) {
        console.error('Error fetching collection:', error);
        return null;
    }
}

async function getAllProductsFallback(
    sortKey: string = 'BEST_SELLING',
    reverse: boolean = false
) {
    try {
        // Map collection sort keys to product sort keys roughly
        const productSortKey = sortKey === 'COLLECTION_DEFAULT' ? 'BEST_SELLING' : sortKey;

        const data = await shopifyFetch<{
            products: { edges: { node: ShopifyProduct }[] };
        }>({
            query: GET_PRODUCTS,
            variables: { first: 50, sortKey: productSortKey as any, reverse },
            tags: ['products'],
        });
        return data.products;
    } catch (error) {
        console.error('Error fetching fallback products:', error);
        return null;
    }
}

export async function generateMetadata({
    params,
}: CollectionPageProps): Promise<Metadata> {
    const { handle } = await params;
    const collection = await getCollectionProducts(handle);

    if (collection) {
        return generateCollectionMetadata(collection.title, collection.description);
    }

    // Fallback metadata
    if (KNOWN_COLLECTIONS[handle]) {
        return {
            title: `${KNOWN_COLLECTIONS[handle]} | Sukhira`,
            description: `Shop the best ${KNOWN_COLLECTIONS[handle]} at Sukhira.`,
        };
    }

    return {
        title: 'Collection Not Found',
    };
}

function getSortParams(sort: string): { sortKey: string; reverse: boolean } {
    switch (sort) {
        case 'price-asc':
            return { sortKey: 'PRICE', reverse: false };
        case 'price-desc':
            return { sortKey: 'PRICE', reverse: true };
        case 'newest':
            return { sortKey: 'CREATED', reverse: true };
        case 'best-selling':
            return { sortKey: 'BEST_SELLING', reverse: false };
        default:
            return { sortKey: 'COLLECTION_DEFAULT', reverse: false };
    }
}

export default async function CollectionPage({
    params,
    searchParams,
}: CollectionPageProps) {
    const { handle } = await params;
    const { sort = 'default' } = await searchParams;
    const { sortKey, reverse } = getSortParams(sort);

    const collection = await getCollectionProducts(handle, sortKey, reverse);
    let products: ShopifyProduct[] = [];
    let title = '';
    let description = '';

    if (collection) {
        products = extractNodes(collection.products.edges);
        title = collection.title;
        description = collection.description;
    } else if (KNOWN_COLLECTIONS[handle]) {
        // Fallback: Fetch all products if collection doesn't exist but is a known route
        const fallbackData = await getAllProductsFallback(sortKey, reverse);
        if (fallbackData) {
            products = extractNodes(fallbackData.edges);
            title = KNOWN_COLLECTIONS[handle];
            description = `Explore our ${KNOWN_COLLECTIONS[handle]} collection.`;
        }
    } else {
        notFound();
    }

    const videoSrc = VIDEO_MAP[handle];

    return (
        <div className="pb-8">
            {/* Video Hero (Dynamic) - Rendered if a video exists for this collection */}
            {videoSrc && (
                <VideoSection
                    title={title}
                    subtitle={description || `Discover our premium ${title.toLowerCase()} collection.`}
                    videoType="self-hosted"
                    videoSrc={videoSrc}
                    layout="overlay"
                />
            )}

            <div className={`container mx-auto px-4 ${videoSrc ? 'mt-16' : 'mt-20 py-8'}`} id="products">
                {/* Header (Only show if no video, to avoid duplication) */}
                {!videoSrc && (
                    <div className="mb-8 text-center md:text-left">
                        <h1 className="text-3xl md:text-5xl font-bold mb-4 tracking-tight">{title}</h1>
                        <p className="text-muted-foreground max-w-2xl text-lg">{description}</p>
                    </div>
                )}

                {/* Toolbar */}
                <div className="flex flex-col sm:flex-row items-start sm:items-center justify-between mb-8 gap-4">
                    <p className="text-sm text-muted-foreground font-medium">
                        {products.length} {products.length === 1 ? 'product' : 'products'}
                    </p>
                    <div className="flex items-center gap-2">
                        <span className="text-sm text-muted-foreground hidden sm:inline">Sort by:</span>
                        <SortSelect currentSort={sort} handle={handle} />
                    </div>
                </div>

                {/* Products Grid */}
                <Suspense fallback={<ProductGridSkeleton />}>
                    {products.length > 0 ? (
                        <ProductGrid products={products} columns={4} />
                    ) : (
                        <div className="text-center py-24 bg-muted/30 rounded-3xl">
                            <p className="text-xl text-muted-foreground">
                                No products found in this collection.
                            </p>
                        </div>
                    )}
                </Suspense>
            </div>
        </div>
    );
}

function SortSelect({ currentSort, handle }: { currentSort: string; handle: string }) {
    return (
        <form>
            <Select defaultValue={currentSort} name="sort">
                <SelectTrigger className="w-[180px] bg-white border-border/50 shadow-sm">
                    <SelectValue placeholder="Sort by" />
                </SelectTrigger>
                <SelectContent>
                    <SelectItem value="default">Featured</SelectItem>
                    <SelectItem value="best-selling">Best Selling</SelectItem>
                    <SelectItem value="newest">Newest</SelectItem>
                    <SelectItem value="price-asc">Price: Low to High</SelectItem>
                    <SelectItem value="price-desc">Price: High to Low</SelectItem>
                </SelectContent>
            </Select>
        </form>
    );
}

'use client';

import { useRouter, useSearchParams } from 'next/navigation';
import { useState, useEffect, useTransition } from 'react';
import { Search, Loader2 } from 'lucide-react';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';

interface SearchFormProps {
    initialQuery: string;
}

export function SearchForm({ initialQuery }: SearchFormProps) {
    const router = useRouter();
    const searchParams = useSearchParams();
    const [query, setQuery] = useState(initialQuery);
    const [isPending, startTransition] = useTransition();

    // Debounced search
    useEffect(() => {
        const timer = setTimeout(() => {
            if (query !== initialQuery) {
                startTransition(() => {
                    if (query) {
                        router.push(`/search?q=${encodeURIComponent(query)}`);
                    } else {
                        router.push('/search');
                    }
                });
            }
        }, 400);

        return () => clearTimeout(timer);
    }, [query, initialQuery, router]);

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        if (query) {
            router.push(`/search?q=${encodeURIComponent(query)}`);
        }
    };

    return (
        <form onSubmit={handleSubmit} className="relative">
            <div className="relative">
                <Search className="absolute left-4 top-1/2 -translate-y-1/2 h-5 w-5 text-muted-foreground" />
                <Input
                    type="search"
                    placeholder="Search products..."
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                    className="pl-12 pr-12 h-12 text-lg"
                    autoFocus
                />
                {isPending && (
                    <Loader2 className="absolute right-4 top-1/2 -translate-y-1/2 h-5 w-5 text-muted-foreground animate-spin" />
                )}
            </div>
        </form>
    );
}

'use client';

import Image from 'next/image';
import Link from 'next/link';
import { motion } from 'framer-motion';
import { Card, CardContent } from '@/components/ui/card';
import { ShopifyCollection } from '@/lib/shopify';

interface CategoryCardsProps {
    collections: ShopifyCollection[];
}

// Animation variants
const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
        opacity: 1,
        transition: { staggerChildren: 0.1, delayChildren: 0.2 },
    },
};

const itemVariants = {
    hidden: { opacity: 0, y: 40 },
    visible: {
        opacity: 1,
        y: 0,
        transition: { duration: 0.6, ease: 'easeOut' as const },
    },
};

const titleVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
        opacity: 1,
        y: 0,
        transition: { duration: 0.6, ease: 'easeOut' as const },
    },
};

export function CategoryCards({ collections }: CategoryCardsProps) {
    return (
        <section className="py-20 md:py-28">
            <div className="container mx-auto px-4">
                <motion.h2
                    variants={titleVariants}
                    initial="hidden"
                    whileInView="visible"
                    viewport={{ once: true }}
                    className="text-3xl md:text-4xl lg:text-5xl font-bold text-center mb-12 tracking-tight"
                >
                    Shop by Category
                </motion.h2>
                <motion.div
                    variants={containerVariants}
                    initial="hidden"
                    whileInView="visible"
                    viewport={{ once: true, margin: '-100px' }}
                    className="grid grid-cols-2 md:grid-cols-4 gap-6 md:gap-8"
                >
                    {collections.map((collection) => (
                        <motion.div key={collection.id} variants={itemVariants}>
                            <Link href={`/collections/${collection.handle}`} className="group block">
                                <Card className="overflow-hidden border-0 shadow-lg hover:shadow-2xl transition-all duration-500 rounded-2xl">
                                    <div className="relative aspect-[4/3] overflow-hidden bg-muted">
                                        {collection.image?.url ? (
                                            <Image
                                                src={collection.image.url}
                                                alt={collection.image.altText || collection.title}
                                                fill
                                                className="object-cover transition-transform duration-700 group-hover:scale-110"
                                                sizes="(max-width: 768px) 50vw, 25vw"
                                            />
                                        ) : (
                                            <div className="absolute inset-0 bg-gradient-to-br from-primary/20 to-primary/50 flex items-center justify-center">
                                                <span className="text-5xl font-bold text-primary/40">
                                                    {collection.title.charAt(0)}
                                                </span>
                                            </div>
                                        )}
                                        <div className="absolute inset-0 bg-gradient-to-t from-black/70 via-black/20 to-transparent group-hover:from-black/80 transition-all duration-500" />
                                        <CardContent className="absolute bottom-0 left-0 right-0 p-6">
                                            <h3 className="font-semibold text-white text-xl group-hover:translate-y-[-4px] transition-transform duration-300">
                                                {collection.title}
                                            </h3>
                                            <p className="text-white/70 text-sm mt-1 opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                                                Explore Collection →
                                            </p>
                                        </CardContent>
                                    </div>
                                </Card>
                            </Link>
                        </motion.div>
                    ))}
                </motion.div>
            </div>
        </section>
    );
}

// Static version with animations
export function CategoryCardsStatic() {
    const staticCategories = [
        { handle: 'home-kitchen', title: 'Home & Kitchen', color: 'from-[#56AF31] to-[#2D5335]' },
        { handle: 'electronics', title: 'Electronics', color: 'from-[#92D5F2] to-[#56AF31]' },
        { handle: 'new-arrivals', title: 'New Arrivals', color: 'from-[#2D5335] to-[#1D1D1B]' },
        { handle: 'sale', title: 'Sale', color: 'from-[#FFE900] to-[#56AF31]' },
    ];

    return (
        <section className="py-20 md:py-28">
            <div className="container mx-auto px-4">
                <motion.h2
                    initial={{ opacity: 0, y: 20 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    viewport={{ once: true }}
                    transition={{ duration: 0.6, ease: [0.25, 0.46, 0.45, 0.94] }}
                    className="text-3xl md:text-4xl lg:text-5xl font-bold text-center mb-12 tracking-tight"
                >
                    Shop by Category
                </motion.h2>
                <motion.div
                    variants={containerVariants}
                    initial="hidden"
                    whileInView="visible"
                    viewport={{ once: true, margin: '-100px' }}
                    className="grid grid-cols-2 md:grid-cols-4 gap-6 md:gap-8"
                >
                    {staticCategories.map((category, index) => (
                        <motion.div
                            key={category.handle}
                            variants={itemVariants}
                            whileHover={{ y: -8 }}
                            transition={{ duration: 0.3 }}
                        >
                            <Link href={`/collections/${category.handle}`} className="group block">
                                <Card className="overflow-hidden border-0 shadow-lg hover:shadow-2xl transition-all duration-500 rounded-2xl">
                                    <div className="relative aspect-[4/3] overflow-hidden">
                                        <div
                                            className={`absolute inset-0 bg-gradient-to-br ${category.color} opacity-90 group-hover:opacity-100 transition-opacity duration-500`}
                                        />
                                        {/* Decorative elements */}
                                        <div className="absolute inset-0 opacity-20">
                                            <div className="absolute top-4 right-4 w-20 h-20 rounded-full bg-white/20 blur-xl" />
                                            <div className="absolute bottom-4 left-4 w-16 h-16 rounded-full bg-white/10 blur-lg" />
                                        </div>
                                        <div className="absolute inset-0 flex flex-col items-center justify-center text-center p-6">
                                            <motion.h3
                                                className="font-bold text-white text-xl md:text-2xl"
                                                whileHover={{ scale: 1.05 }}
                                            >
                                                {category.title}
                                            </motion.h3>
                                            <p className="text-white/80 text-sm mt-2 opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                                                Explore →
                                            </p>
                                        </div>
                                    </div>
                                </Card>
                            </Link>
                        </motion.div>
                    ))}
                </motion.div>
            </div>
        </section>
    );
}

'use client';

import Image from 'next/image';
import Link from 'next/link';
import { X, Check, Minus, ShoppingCart } from 'lucide-react';
import {
    Table,
    TableBody,
    TableCell,
    TableHead,
    TableHeader,
    TableRow,
} from '@/components/ui/table';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { useCompare } from '@/context/CompareContext';
import { useCart } from '@/context/CartContext';
import { formatPrice } from '@/lib/money';

export function CompareTable() {
    const { items, removeFromCompare, clearCompare, isLoading } = useCompare();
    const { addToCart } = useCart();

    if (isLoading) {
        return (
            <div className="animate-pulse space-y-4">
                <div className="h-8 bg-muted rounded w-1/4" />
                <div className="h-64 bg-muted rounded" />
            </div>
        );
    }

    if (items.length === 0) {
        return (
            <div className="text-center py-12">
                <p className="text-lg font-medium mb-2">No products to compare</p>
                <p className="text-muted-foreground mb-6">
                    Add up to 4 products to compare their features.
                </p>
                <Button asChild>
                    <Link href="/collections/all">Browse Products</Link>
                </Button>
            </div>
        );
    }

    const features = [
        { key: 'price', label: 'Price' },
        { key: 'vendor', label: 'Brand' },
        { key: 'productType', label: 'Category' },
        { key: 'availability', label: 'Availability' },
        { key: 'variants', label: 'Variants' },
        { key: 'tags', label: 'Features' },
    ];

    const renderFeatureValue = (item: typeof items[0], key: string) => {
        switch (key) {
            case 'price':
                return (
                    <div className="space-y-1">
                        <span className="font-bold text-lg">{formatPrice(item.price)}</span>
                        {item.compareAtPrice && parseFloat(item.compareAtPrice) > parseFloat(item.price) && (
                            <span className="text-sm text-muted-foreground line-through block">
                                {formatPrice(item.compareAtPrice)}
                            </span>
                        )}
                    </div>
                );
            case 'vendor':
                return item.vendor || <Minus className="h-4 w-4 text-muted-foreground" />;
            case 'productType':
                return item.productType || <Minus className="h-4 w-4 text-muted-foreground" />;
            case 'availability':
                return item.availableForSale ? (
                    <Badge variant="outline" className="bg-green-50 text-green-700 border-green-200">
                        <Check className="h-3 w-3 mr-1" />
                        In Stock
                    </Badge>
                ) : (
                    <Badge variant="outline" className="bg-red-50 text-red-700 border-red-200">
                        <X className="h-3 w-3 mr-1" />
                        Out of Stock
                    </Badge>
                );
            case 'variants':
                return (
                    <span className="text-sm">
                        {item.variants.length} {item.variants.length === 1 ? 'option' : 'options'}
                    </span>
                );
            case 'tags':
                return item.tags.length > 0 ? (
                    <div className="flex flex-wrap gap-1">
                        {item.tags.slice(0, 3).map((tag) => (
                            <Badge key={tag} variant="secondary" className="text-xs">
                                {tag}
                            </Badge>
                        ))}
                        {item.tags.length > 3 && (
                            <Badge variant="secondary" className="text-xs">
                                +{item.tags.length - 3}
                            </Badge>
                        )}
                    </div>
                ) : (
                    <Minus className="h-4 w-4 text-muted-foreground" />
                );
            default:
                return <Minus className="h-4 w-4 text-muted-foreground" />;
        }
    };

    return (
        <div className="space-y-4">
            <div className="flex justify-end">
                <Button variant="outline" onClick={clearCompare}>
                    Clear All
                </Button>
            </div>

            <div className="overflow-x-auto">
                <Table>
                    <TableHeader>
                        <TableRow>
                            <TableHead className="w-40 sticky left-0 bg-background z-10">
                                Feature
                            </TableHead>
                            {items.map((item) => (
                                <TableHead key={item.productId} className="min-w-[200px] text-center">
                                    <div className="space-y-3">
                                        <div className="relative">
                                            <Button
                                                variant="ghost"
                                                size="icon"
                                                className="absolute -top-2 -right-2 h-6 w-6"
                                                onClick={() => removeFromCompare(item.productId)}
                                            >
                                                <X className="h-4 w-4" />
                                            </Button>
                                            <Link href={`/products/${item.handle}`}>
                                                <div className="relative w-32 h-32 mx-auto bg-muted rounded-lg overflow-hidden">
                                                    {item.image ? (
                                                        <Image
                                                            src={item.image}
                                                            alt={item.title}
                                                            fill
                                                            className="object-cover"
                                                            sizes="128px"
                                                        />
                                                    ) : (
                                                        <div className="w-full h-full flex items-center justify-center text-muted-foreground">
                                                            No Image
                                                        </div>
                                                    )}
                                                </div>
                                            </Link>
                                        </div>
                                        <Link
                                            href={`/products/${item.handle}`}
                                            className="font-medium text-sm hover:text-primary transition-colors line-clamp-2"
                                        >
                                            {item.title}
                                        </Link>
                                    </div>
                                </TableHead>
                            ))}
                        </TableRow>
                    </TableHeader>
                    <TableBody>
                        {features.map((feature) => (
                            <TableRow key={feature.key}>
                                <TableCell className="font-medium sticky left-0 bg-background z-10">
                                    {feature.label}
                                </TableCell>
                                {items.map((item) => (
                                    <TableCell key={item.productId} className="text-center">
                                        {renderFeatureValue(item, feature.key)}
                                    </TableCell>
                                ))}
                            </TableRow>
                        ))}
                        <TableRow>
                            <TableCell className="font-medium sticky left-0 bg-background z-10">
                                Actions
                            </TableCell>
                            {items.map((item) => (
                                <TableCell key={item.productId} className="text-center">
                                    <div className="flex flex-col gap-2">
                                        <Button asChild size="sm">
                                            <Link href={`/products/${item.handle}`}>View Details</Link>
                                        </Button>
                                        {item.availableForSale && (
                                            <Button
                                                variant="outline"
                                                size="sm"
                                                onClick={() => {
                                                    // Need variant ID - link to product page for now
                                                    window.location.href = `/products/${item.handle}`;
                                                }}
                                            >
                                                <ShoppingCart className="h-4 w-4 mr-2" />
                                                Add to Cart
                                            </Button>
                                        )}
                                    </div>
                                </TableCell>
                            ))}
                        </TableRow>
                    </TableBody>
                </Table>
            </div>
        </div>
    );
}

'use client';

import Link from 'next/link';
